import styled from 'styled-components';

export const Container = styled.main`
    background: rgba(173, 190, 167, 80%);
    width: 30%;
    height: 100%;
    bottom: 0;

`

export const Content = styled.div`
    width: 100%;

    h1 {
        text-transform: uppercase;
        color: var(--blue);
        font-size: 4rem;
        text-align: center;
    }

`;
