import styled from 'styled-components';

export const Content = styled.div`
    text-align: center;

    input[type="text"]{
        border:0;
        border-radius: 10px;
        font-size: 1rem;
        height: 3rem;
        padding: 15px;
        outline: none;
    }
`;
